mapper.select:
select count(*)
from user
where id=#{id}

    	select id, pw, name, email, auth, warning, myHospital_id
		from user
		where id=#{id}

select id
from user
where email = #{email}

SELECT id, pw, name, email, auth, warning, myHospital_id
FROM user
WHERE name=#{name} AND id=#{id} AND email=#{email}

mapper.update:
UPDATE user
SET pw=#{pw}
WHERE id=#{id}
